const {
  argv: {
    ws
  }
} = _yargs.default.usage('Usage: [options]').wrap(120).options({
  ws: {
    default: 'ws://127.0.0.1:9944',
    description: 'The API endpoint to connect to, e.g. wss://poc3-rpc.polkadot.io',
    type: 'string',
    required: true
  }
});

async function main() {
  const provider = new _index.WsProvider(ws);
  const api = await _index.ApiPromise.create({
    provider
  });
  const chain = await api.rpc.system.chain();
  const props = await api.rpc.system.properties();
  const meta = await api.rpc.state.getMetadata(); // output the chain info, for easy re-use

  console.error("export default { chain: '".concat(chain.toString(), "', genesisHash: '").concat(api.genesisHash.toHex(), "', ss58Format: ").concat(props.ss58Format.unwrapOr(42), ", tokenDecimals: ").concat(props.tokenDecimals.unwrapOr(0), ", tokenSymbol: '").concat(props.tokenSymbol.unwrapOr('UNIT'), "', metaCalls: '").concat(Buffer.from(meta.asCallsOnly.toU8a()).toString('base64'), "' };")); // show any missing types

  meta.getUniqTypes(false);
}

